import { RARITIES, TRIGGER_EVENTS, DAMAGE_TYPES } from "../../core/constants";

const relicList = [
  {
    name: "Magic Scroll",
    rarity: RARITIES.COMMON,
    triggers: {
      [TRIGGER_EVENTS.RELIC_PICKUP]: {
        bonusPages: 1,
      },
    },
  },
  {
    name: "Magic Wand",
    rarity: RARITIES.COMMON,
    triggers: {
      [TRIGGER_EVENTS.RELIC_PICKUP]: {
        bonusBaseBunnies: 3,
      },
    },
  },
  // {
  //   name: "Faberg√© Egg",
  //   rarity: RARITIES.COMMON,
  //   triggers: {
  //     [TRIGGER_EVENTS.RELIC_PICKUP]: {
  //       bonusGold: 100,
  //     },
  //   },
  // },
  {
    name: "Heartstone",
    rarity: RARITIES.COMMON,
    triggers: {
      [TRIGGER_EVENTS.RELIC_PICKUP]: {
        bonusHealth: 50,
      },
    },
  },
  {
    name: "Cowbell",
    rarity: RARITIES.UNCOMMON,
    triggers: {
      [TRIGGER_EVENTS.RELIC_PICKUP]: {
        BonusMulligans: 1,
      },
    },
  },
  // {
  //   // fix to reduce damage taken on combat loss.
  //   name: "Protective Amulet",
  //   rarity: RARITIES.UNCOMMON,
  //   triggers: {
  //     [TRIGGER_EVENTS.RELIC_PICKUP]: {
  //       bonusHealth: 10,
  //     },
  //   },
  // },
  {
    name: "Encyclopedia",
    rarity: RARITIES.MYTHIC,
    triggers: {
      [TRIGGER_EVENTS.RELIC_PICKUP]: {
        bonusBooks: 1,
      },
    },
  },
  {
    name: "Inkpot",
    rarity: RARITIES.MYTHIC,
    triggers: {
      [TRIGGER_EVENTS.RELIC_PICKUP]: {
        bonusInk: 2,
      },
    },
  },
  {
    name: "Cardsleeves",
    rarity: RARITIES.MYTHIC,
    triggers: {
      [TRIGGER_EVENTS.RELIC_PICKUP]: {
        permanentlyUpgradeRandomCardsInDeck: 5,
      },
    },
  },
  // {
  //   name: "Magic Quill",
  //   rarity: RARITIES.LEGENDARY,
  //   triggers: {
  //     [TRIGGER_EVENTS.RELIC_PICKUP]: {
  //       bonusInk: 2,
  //     },
  //   },
  // },
  // {
  //   name: "Gold Bag",
  //   rarity: RARITIES.BASIC_POLY,
  //   triggers: {
  //     [TRIGGER_EVENTS.RELIC_PICKUP]: {
  //       bonusGold: 25,
  //     },
  //   },
  // },
  {
    name: "Whetstone",
    rarity: RARITIES.MYTHIC,
    triggers: {
      [TRIGGER_EVENTS.CARD_PICKUP]: {
        upgradeCard: true,
      },
    },
  },
  {
    name: "Porcelain Koi",
    rarity: RARITIES.MYTHIC,
    triggers: {
      [TRIGGER_EVENTS.CARD_PICKUP]: {
        bonusGold: 5,
        bonusHealth: 20,
      },
    },
  },

  {
    name: "Witch's Cauldron",
    rarity: RARITIES.COMMON,
    triggers: {
      [TRIGGER_EVENTS.POTION_PICKUP]: {
        upgradePotion: true,
      },
    },
  },
  {
    name: "Glittering Vial",
    rarity: RARITIES.UNCOMMON,
    triggers: {
      [TRIGGER_EVENTS.DRINK_POTION]: {
        healPlayer: 5,
      },
    },
  },
  // {
  //   name: "Discount Voucher",
  //   rarity: RARITIES.COMMON,
  //   triggers: {
  //     [TRIGGER_EVENTS.ASSIGN_SHOP_PRICES]: {
  //       shopPriceMultiplier: 0.8, // 20% cheaper shop prices
  //     },
  //   },
  // },
  // {
  //   name: "Sleeping Bag",
  //   rarity: RARITIES.COMMON,
  //   triggers: {
  //     [TRIGGER_EVENTS.REST]: {
  //       healPlayer: 50,
  //     },
  //   },
  // },
  // {
  //   name: "Toothfairy's Charm",
  //   rarity: RARITIES.COMMON,
  //   triggers: {
  //     [TRIGGER_EVENTS.REST]: {
  //       goldAdd: 50,
  //     },
  //   },
  // },
  // {
  //   name: "Planetarium Mobile",
  //   rarity: RARITIES.UNCOMMON,
  //   triggers: {
  //     [TRIGGER_EVENTS.REST]: {
  //       permanentlyUpgradeRandomCardsInDeck: 2, // upgrade random cards in the deck when resting
  //     },
  //   },
  // },
  {
    name: "Dousing Rod",
    rarity: RARITIES.COMMON,
    triggers: {
      [TRIGGER_EVENTS.POPULATE_PATHS]: {
        revealAnonymousPaths: true,
      },
    },
    nonDuplicable: true,
  },
  {
    name: "Gem of Weakness",
    rarity: RARITIES.RARE,
    triggers: {
      [TRIGGER_EVENTS.COMBAT_START]: {
        weakenEnemyHpPercent: 0.1, // 10% reduction
      },
    },
  },
  {
    name: "Phylactery",
    rarity: RARITIES.RARE,
    triggers: {
      [TRIGGER_EVENTS.SHUFFLE_GRAVEYARD_INTO_DECK]: {
        healPlayer: 5,
      },
    },
  },
  {
    name: "Crystal Ball",
    rarity: RARITIES.MYTHIC,
    triggers: {
      [TRIGGER_EVENTS.DRAW_CARD]: {
        bunnyAdd: 1,
      },
    },
  },
  {
    name: "Arcane Brush",
    rarity: RARITIES.RARE,
    triggers: {
      [TRIGGER_EVENTS.PLAY_CARD]: {
        bunnyAdd: 2,
      },
    },
  },
  {
    name: "Golden Carrot",
    rarity: RARITIES.LEGENDARY,
    bossOnly: true,
    triggers: {
      [TRIGGER_EVENTS.RELIC_PICKUP]: {
        bonusBooks: 1,
        bonusPages: 1,
        bonusInk: 1,
      },
    },
  },
  {
    name: "Voynich Manuscript",
    rarity: RARITIES.RARE,
    triggers: {
      [TRIGGER_EVENTS.CAST_SPELLBOOK]: {
        healPlayer: 5,
      },
    },
  },
  {
    name: "Corsair's Coin",
    rarity: RARITIES.COMMON,
    triggers: {
      [TRIGGER_EVENTS.COMBAT_END]: {
        goldAdd: 25,
      },
    },
  },
  {
    name: "Carrot Staff",
    rarity: RARITIES.MYTHIC,
    description: "increases all Bunny damage you deal.",
    triggers: {
      [TRIGGER_EVENTS.DEAL_DAMAGE]: {
        damageTypeTrigger: DAMAGE_TYPES.BUNNY,
        multiplyDamage: 1.5,
      },
    },
  },
  {
    name: "Lightning Rod",
    rarity: RARITIES.MYTHIC,
    description: "Whenever you cast a Lightning spell, draw 2 cards.",
    triggers: {
      [TRIGGER_EVENTS.PLAY_CARD]: {
        ifLightningDrawCards: 2,
      },
    },
  },
  // {
  //   name: "Firemage's Hat",
  //   rarity: RARITIES.MYTHIC,
  //   description: "All Fire cards cost 1 less ink.",
  //   triggers: {
  //     [TRIGGER_EVENTS.RELIC_PICKUP]: {
  //       reduceInkCostOfFireCardsInDeck: 1,
  //     },
  //     [TRIGGER_EVENTS.CARD_PICKUP]: {
  //       reduceInkCostIfFire: 1,
  //     },
  //   },
  // },
  {
    name: "Thinking Cap",
    rarity: RARITIES.MYTHIC,
    description: "Your hand size is permanently increased by 3.",
    triggers: {
      [TRIGGER_EVENTS.RELIC_PICKUP]: {
        bonusHandSize: 3,
      },
    },
  },
  {
    name: "Silk Gloves",
    rarity: RARITIES.COMMON,
    description: "Your hand size is permanently increased by 1.",
    triggers: {
      [TRIGGER_EVENTS.RELIC_PICKUP]: {
        bonusHandSize: 1,
      },
    },
  },
  {
    name: "Ring of Athena",
    rarity: RARITIES.RARE,
    description: "Your hand size is permanently increased by 2.",
    triggers: {
      [TRIGGER_EVENTS.RELIC_PICKUP]: {
        bonusHandSize: 2,
      },
    },
  },
];
exports.relicList = relicList;
